import React, { useState, useEffect } from 'react';

function MyPage(props) {

    const [categoryID, setCategoryID] = useState("");

    useEffect(() => {
        let element = document.querySelector(".mypage .categories .categoryItem");

        element.classList.add("active");

        setCategoryID(element.name);
    }, []);

    function setCategory(event) {
        if (!event) return;
        let elements = document.querySelectorAll(".mypage .categories .categoryItem");

        for (let i = 0; i < elements.length; i++) {
            elements[i].classList.remove("active");
        }

        event.target.parentElement.classList.add("active");

        setCategoryID(event.target.name);
    }

    function getBody() {
        switch (categoryID) {
            case "myInformation":
                break;
            case "myAccount":
                break;
            case "myDeposit":
                break;
            case "myCredits":
                break;
            default:
                return null;
        }
    }

    return (
        <div className="mypage">
            <div className="title">
                <h2>
                    Мої дані
                </h2>
            </div>
            <div className="categories">
                <div className="categoryItem">
                    <a className="btn linkButton" onClick={setCategory} name="myInformation">
                        Моя інформація
                    </a>
                </div>
                <div className="categoryItem">
                    <a className="btn linkButton" onClick={setCategory} name="myAccount">
                        Мої рахунки
                    </a>
                </div>
                <div className="categoryItem">
                    <a className="btn linkButton" onClick={setCategory} name="myDeposit">
                        Депозити
                    </a>
                </div>
                <div className="categoryItem">
                    <a className="btn linkButton" onClick={setCategory} name="myCredits">
                        Кредити
                    </a>
                </div>
            </div>
            <div className="items">
                {
                    getBody
                }
            </div>
        </div>
    );
}

export default MyPage;
