import React, { useEffect, useState } from 'react';

function scroll() {
    if (document.documentElement.scrollTop > 100) {
        document.querySelector("header").classList.add("stick");
    }
    else {
        document.querySelector("header").classList.remove("stick");
    }
}

function Header() {
    const [user, setUser] = useState("");

    useEffect(() => {
        window.onscroll = scroll;
    });

    useEffect(() => {
        let href = document.location.href;
        let elements = document.querySelectorAll("header .menu li a");
        for (let i = 0; i < elements.length; i++) {
            if (elements[i].href === href) {
                elements[i].parentElement.classList.add("active");
            }
            else {
                elements[i].parentElement.classList.remove("active");
            }
        }
    });

    useEffect(() => {
        let user = JSON.parse(localStorage.getItem("BankingUser"));
        if (!user) {
            user = JSON.parse(sessionStorage.getItem("BankingUser"));
        }
        setUser(user);
        debugger
    });

    return (
        <header>
            <div className="wrapper">
                <div className="logoZone">
                    <h1>
                        <a href="/">
                            Banking
                            <span className="redDot">.</span>
                        </a>
                    </h1>
                </div>
                <div className="menu">
                    <ul className="menu">
                        <li>
                            <a href="/">
                                Головна
                            </a>
                        </li>
                        <li>
                            <a href="/addaccount">
                                Відкрити рахунок
                            </a>
                        </li>
                        <li>
                            <a href="/contact">
                                Контакти
                            </a>
                        </li>
                        <li>
                            <a href="/login">
                                Вхід
                            </a>
                        </li>
                    </ul>
                </div>
            </div>
        </header>
    );
}

export default Header;
