                        </div>
                        <label>
                            Четверта сторінка паспорту
                        </label>
                        {
                            myDocs.passportData && myDocs.passportData.isPPassportImageAddressPage2Right ?
                                <label>
                                    <i className="fa fa-like"></i>
                                    Перевірено
                                </label>
                                :
                                <label>
                                    <i className="fa fa-close"></i>
                                    Не підтверджено
                                </label>
                        }
                        <input onChange={(event) => {
                            let file = event.target.files[0];

