import React from "react";
import ReactDOM from "react-dom";
import "bootstrap/dist/css/bootstrap.css";
import "./styles.css";
import { createBrowserHistory } from "history";
import { Provider } from "react-redux";
import { ConnectedRouter } from "react-router-redux";

import { BrowserRouter as Router, Route, Switch } from "react-router-dom";

import configureStore from "./store/configureStore";

import Banner from './Common/Banner';
import logo from "./SiteFiles/banner.webp";

import HomePage from './HomePage/HomePage';
import Header from './Header & Footer/Header';
import Footer from './Header & Footer/Footer';
import Login from './LoginPage/Login';
import Registration from './LoginPage/Registration';

const baseUrl = document.getElementsByTagName("base")[0].getAttribute("href");
const history = createBrowserHistory({ basename: baseUrl });

const initialState = window.initialReduxState;
const store = configureStore(history, initialState);

class MainComponent extends React.Component {
  constructor (props) {
    super(props);

    this.state = {
      isUserLogined: false
    }
  }

  componentDidMount() {
    let user = localStorage.getItem("BankingUser");
    if (!user){
      user = sessionStorage.getItem("BankingUser");
    }
    if (user) {
      this.setState({isUserLogined: true});
    }
    else {
      this.setState({isUserLogined: false});
    }
  }

  render() {
    return (
      <Router>
        <div className="container-fluid">
          <Header />
          <main>
            <Banner image={logo} title={"Найкращий банк для тебе"} description={"Обирай для себе та приводь друзів"} />
            <Switch>
              <Route exact path="/"  component={HomePage} />
              {
                this.state.isUserLogined ?
                <Route path="/registration" exact component={NoMatch}/> 
                :
                <Route path="/registration" exact component={Registration} />
              }
              {
                this.state.isUserLogined ?
                <Route path="/login" exact component={NoMatch}/>  
                :
                <Route path="/login" exact component={Login} />
              }
              <Route component={NoMatch} />
            </Switch>
          </main>
          <Footer />
        </div>
      </Router>
    );
  }
}
function NoMatch({ location }) {
  return (
    <div>
      <h1>HTTP 404 page not found</h1>
      <h3>
        No match for <code>{location.pathname}</code>
      </h3>
    </div>
  );
}
function NoAccess({ location }) {
  return (
    <div>
      <h1>You do not have permissions to this page</h1>
    </div>
  );
}

ReactDOM.render(
  <Provider store={store}>
    <ConnectedRouter history={history}>
      <MainComponent />
    </ConnectedRouter>
  </Provider>,

  document.getElementById("root")
);
