import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getUsers } from '../actions';

function Users(props) {

    const usersList = useSelector(state => state.users);
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(getUsers());
    }, []);

    useEffect(() => {
        if (usersList && usersList.result) {
            if (usersList.result.success) {
                console.log("users", usersList.result.users)
            }
            else {
                console.log(usersList.result.message);
            }
        }
    }, [usersList]);

    return (
        <div className="usersPage">
            <table>
                <thead>
                    <tr>
                        <td>
                            ID
                        </td>
                        <td>
                            Ім'я
                        </td>
                        <td>
                            Емейл
                        </td>
                        <td>
                            Роль
                        </td>
                        <td>
                            Вік
                        </td>
                        <td>
                            Країна
                        </td>
                        <td>
                            Місто
                        </td>
                        <td>
                            Вулиця
                        </td>
                        <td>
                            Будинок
                        </td>
                        <td>
                            Власний
                        </td>
                        <td>
                            Квартира
                        </td>
                        <td>
                            Активний
                        </td>
                    </tr>
                </thead>
                <tbody>
                    {
                        usersList && usersList.result && usersList.result.users && usersList.result.users.length > 0 ?
                            usersList.result.users.map((item, key) =>
                                <tr className={"row-" + key} key={"item" + key}>
                                    <td>
                                        {item.id}
                                    </td>
                                    <td>
                                        {item.name + " " + item.secondName}
                                    </td>
                                    <td>
                                        {item.email}
                                    </td>
                                    <td>
                                        {
                                            item.roleID === 1 ?
                                                "Адміністратор"
                                                :
                                                item.roleID === 2 ?
                                                    "Працівник" :
                                                    item.roleID === 3 ?
                                                        "Клієнт"
                                                        :
                                                        ""
                                        }
                                    </td>
                                    <td>
                                        {item.age}
                                    </td>
                                    <td>
                                        {item.country}
                                    </td>
                                    <td>
                                        {item.city}
                                    </td>
                                    <td>
                                        {item.street}
                                    </td>
                                    <td>
                                        {item.houseNumber}
                                    </td>
                                    <td>
                                        {item.isPrivateHouse ? "Так" : "Ні"}
                                    </td>
                                    <td>
                                        {item.appartmentsNumber}
                                    </td>
                                    <td>
                                        {item.isActive ? "Так" : "Ні"}
                                    </td>

                                </tr>
                            )
                            :
                            null
                    }
                </tbody>
            </table>
        </div>
    );
}

export default Users;
