import React, { useState } from 'react';

export default function AddCredit(props) {

    const [showSaveButton, setShowSave] = useState(false);
    const [showAdd, setShowAdd] = useState(false);
    const [type, setType] = useState(1);
    const [monthes, setMonthes] = useState(6);
    const [sum, setSum] = useState(0);
    const [fullSum, setFullSum] = useState(0);
    const [monthPay, setMonthPay] = useState(0);

    function discard() {
        setShowAdd(false);
    }

    function proceed() {
        if (!type || type === ""
            || !monthes || monthes === ""
            || !sum || sum === "") {
            window.alert("Потрібно заповнити всі поля!");
            return;
        }

        setShowSave(true);

        switch (type) {
            case 1:
                setFullSum(sum * Math.pow((1 + 8 / 100), monthes / 12));
                setMonthPay(fullSum / monthes);
                break;
            case 2:
                setFullSum(sum * Math.pow((1 + 12 / 100), monthes / 12));
                setMonthPay(fullSum / monthes);
                break;
            case 3:
                setFullSum(sum * Math.pow((1 + 25 / 100), monthes / 12));
                setMonthPay(fullSum / monthes);
                break;
            case 4:
                setFullSum(sum * Math.pow((1 + 22 / 100), monthes / 12));
                setMonthPay(fullSum / monthes);
                break;
        }
    }

    return (
        <div className="addCredit">
            <div className="addButton">
                <button className="btn linkButton" onClick={() => setShowAdd(true)}>
                    <i className="fa fa-plus"></i>
                    <span> Додати</span>
                </button>
            </div>
            {
                showAdd ?
                    <div className="addBody">
                        <div className="form-group row">
                            <div className="col-sm-4">
                                <label>
                                    Тип кредиту
                                </label>
                                <select className="form-control" onChange={(event) => { setType(parseInt(event.target.value)) }}>
                                    <option value="1">На житло</option>
                                    <option value="2">На машину</option>
                                    <option value="3">Готівковий</option>
                                    <option value="4">На картку</option>
                                </select>
                            </div>
                            <div className="col-sm-4">
                                <label>
                                    Строк в місяцях
                                </label>
                                <select className="form-control" onChange={(event) => { setMonthes(parseInt(event.target.value)) }}>
                                    <option value="6">6 місяців</option>
                                    <option value="12">12 місяців</option>
                                    <option value="18">18 місяців</option>
                                    <option value="24">24 місяців</option>
                                    <option value="48">48 місяців</option>
                                    <option value="96">96 місяців</option>
                                </select>
                            </div>
                            <div className="col-sm-4">
                                <label>
                                    Сума (грн)
                                </label>
                                <input type="text" className="form-control" onChange={(event) => setSum(parseFloat(event.target.value))} />
                            </div>
                        </div>
                        <div className="form-group buttons row">
                            <div className="col-sm-12">
                                <input type="button" value="Продовжити" className="btn linkButton" onClick={() => proceed()} />
                            </div>
                        </div>
                        {
                            showSaveButton ?
                                <div className="form-group buttons row">
                                    <div className="col-sm-4">
                                        <label>
                                            Загальна сума кредиту
                                        </label>
                                        <input type="text" value={fullSum} />
                                    </div>
                                    <div className="col-sm-4">
                                        <label>
                                            Щомісячний платіж
                                        </label>
                                        <input type="text" value={monthPay} />
                                    </div>
                                    <div className="col-sm-4">
                                        <input type="button" value="Зберегти" className="btn linkButton" />
                                    </div>
                                </div>
                                :
                                null
                        }
                        <div className="form-group buttons row">
                            <div className="col-sm-12">
                                <input type="button" value="Скасувати" className="btn linkButton" onClick={() => discard()} />
                            </div>
                        </div>
                    </div>
                    :
                    null
            }
        </div>
    );
}
