import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getTypes } from './actions';

function AddAccount(props) {

    const [type, setType] = useState("");
    const [choise, setChoise] = useState("");

    const dispatch = useDispatch();
    const creditTypes = useSelector(state => state.user);

    useEffect(() => {
        dispatch(getTypes());
    }, []);

    function proceed() {

    }

    return (
        <div className="addAccount">
            <div className="types">
                <div className="form-group">
                    <div className="co-sm-12">
                        <h4>
                            Оберіть тип картки
                        </h4>
                    </div>
                    <div className="col-sm-6">
                        <select className="form-control" onSelect={(event) => { debugger }}>
                            {
                                creditTypes && creditTypes.cardTypes ?
                                    creditTypes.cardTypes.cardTypes.map((item, key) =>
                                        <option key={key} value={item.id}>
                                            {item.type}
                                        </option>
                                    )
                                    :
                                    null
                            }
                        </select>
                    </div>
                    <div className="col-sm-6">
                        <input type="button" className="btn linkButton" value="Обрати" onClick={() => proceed()} />
                    </div>
                </div>
                <div className="form-group">
                    <div className="col-sm-12">
                        <button className="btn linkButton">
                            Зберегти
                        </button>
                        <button className="btn linkButton" onClick={props.cancel}>
                            Скасувати
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default AddAccount;
