import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';

function scroll() {
    if (document.documentElement.scrollTop > 100) {
        document.querySelector("header").classList.add("stick");
    }
    else {
        document.querySelector("header").classList.remove("stick");
    }
}

function Header() {

    const logined = useSelector(state => state.authentificateUser);

    useEffect(() => {
        window.onscroll = scroll;
    }, []);

    useEffect(() => {
        let href = document.location.href;
        let elements = document.querySelectorAll("header .menu li a");
        for (let i = 0; i < elements.length; i++) {
            if (elements[i].href === href) {
                elements[i].parentElement.classList.add("active");
            }
            else {
                elements[i].parentElement.classList.remove("active");
            }
        }
    }, []);

    function endSession() {
        localStorage.removeItem("BankingUser");
        sessionStorage.removeItem("BankingUser");

        window.alert("Успіх");
        window.location.href = "/";
    }

    return (
        <header>
            <div className="wrapper">
                <div className="logoZone">
                    <h1>
                        <a href="/">
                            Banking
                            <span className="redDot">.</span>
                        </a>
                    </h1>
                </div>
                <div className="menu">
                    <ul className="menu">
                        <li>
                            <a href="/">
                                Головна
                            </a>
                        </li>
                        <li>
                            <a href="/addaccount">
                                Відкрити рахунок
                            </a>
                        </li>
                        <li>
                            <a href="/contact">
                                Контакти
                            </a>
                        </li>
                        {
                            !logined ?
                                <li>
                                    <a href="/login">
                                        Вхід
                                    </a>
                                </li>
                                :
                                <li>
                                    <a onClick={endSession} href="#">
                                        Вихід
                                    </a>
                                </li>
                        }
                    </ul>
                </div>
            </div>
        </header>
    );
}

export default Header;
